package kr.ac.kopo.day0311;

public class Lec0311 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] arr = new int[5];
		for (int i = 0, num = 10; i < 5; i++, num += 10) {
			arr[i] = num;
		}
		
		
		for (int i=0;i<5;i++) {
			System.out.println(i + "번 값은 " + arr[i] + "입니다" );
		}
		
		
		
		
		
		
	}

}
